import { useState, useEffect, useCallback } from 'react';
import { Link } from 'react-router-dom';
import LibraryButton from './LibraryButton';

export default function UserLibrary({ id }) {
  const [library, setLibrary] = useState([]);
  const [loading, setLoading] = useState(true);
  const [filter, setFilter] = useState('all'); // 'all', 'owned', 'wishlist'

  const fetchLibrary = useCallback(async () => {
    setLoading(true);
    try {
      const res = await fetch(`http://localhost:4000/users/${id}/library`);
      const data = await res.json();
      setLibrary(data);
      setLoading(false);
    } catch (err) {
      console.error(err);
      setLoading(false);
    }
  }, [id]);

  useEffect(() => {
    fetchLibrary();
  }, [fetchLibrary]);

  const filteredLibrary = library.filter(game => {
    if (filter === 'owned') return game.ownership_status === 'owned';
    if (filter === 'wishlist') return game.ownership_status === 'wishlist';
    return true;
  });

  console.log('Library games:', filteredLibrary); // ADD THIS LINE

  const ownedCount = library.filter(g => g.ownership_status === 'owned').length;
  const wishlistCount = library.filter(g => g.ownership_status === 'wishlist').length;

  if (loading) {
    return <p style={{ textAlign: 'center', padding: '2rem' }}>Loading library...</p>;
  }

  return (
    <div>
      <div style={{
        display: 'flex',
        justifyContent: 'space-between',
        alignItems: 'center',
        marginBottom: '2rem',
        flexWrap: 'wrap',
        gap: '1rem'
      }}>
        <h1>My Library</h1>
        
        <div style={{
          display: 'flex',
          gap: '0.5rem',
          backgroundColor: 'var(--bg-card)',
          padding: '0.25rem',
          borderRadius: '8px',
          border: '1px solid var(--border-color)'
        }}>
          <button
            onClick={() => setFilter('all')}
            style={{
              padding: '0.5rem 1rem',
              backgroundColor: filter === 'all' ? 'var(--accent-primary)' : 'transparent',
              color: filter === 'all' ? 'white' : 'var(--text-primary)',
              border: 'none',
              borderRadius: '6px',
              cursor: 'pointer',
              fontWeight: filter === 'all' ? '600' : '400'
            }}
          >
            All ({library.length})
          </button>
          <button
            onClick={() => setFilter('owned')}
            style={{
              padding: '0.5rem 1rem',
              backgroundColor: filter === 'owned' ? 'var(--success)' : 'transparent',
              color: filter === 'owned' ? 'white' : 'var(--text-primary)',
              border: 'none',
              borderRadius: '6px',
              cursor: 'pointer',
              fontWeight: filter === 'owned' ? '600' : '400'
            }}
          >
            Owned ({ownedCount})
          </button>
          <button
            onClick={() => setFilter('wishlist')}
            style={{
              padding: '0.5rem 1rem',
              backgroundColor: filter === 'wishlist' ? 'var(--warning)' : 'transparent',
              color: filter === 'wishlist' ? 'white' : 'var(--text-primary)',
              border: 'none',
              borderRadius: '6px',
              cursor: 'pointer',
              fontWeight: filter === 'wishlist' ? '600' : '400'
            }}
          >
            Wishlist ({wishlistCount})
          </button>
        </div>
      </div>

      {filteredLibrary.length === 0 ? (
        <div style={{
          textAlign: 'center',
          padding: '3rem',
          backgroundColor: 'var(--bg-card)',
          borderRadius: '8px',
          border: '1px solid var(--border-color)'
        }}>
          <p style={{ fontSize: '1.2rem', color: 'var(--text-secondary)', marginBottom: '1rem' }}>
            {filter === 'all' 
              ? 'Your library is empty' 
              : `No games in ${filter}`}
          </p>
          <Link to="/" className="btn btn-primary">
            Browse Games
          </Link>
        </div>
      ) : (
        <div className="games-grid">
          {filteredLibrary.map((game) => (
            <div key={game.game_id} className="game-card">
              <Link to={`/games/${game.game_id}`} style={{ textDecoration: 'none', flex: 1 }}>
                <div className="game-cover">
                  <div className="game-cover-placeholder">
                    {game.title.charAt(0)}
                  </div>
                  <div style={{
                    position: 'absolute',
                    top: '0.5rem',
                    right: '0.5rem',
                    padding: '0.25rem 0.5rem',
                    backgroundColor: game.ownership_status === 'owned' ? 'var(--success)' : 'var(--warning)',
                    borderRadius: '4px',
                    fontSize: '0.7rem',
                    fontWeight: '600',
                    color: 'white'
                  }}>
                    {game.ownership_status === 'owned' ? '✓ Owned' : '⭐ Wishlist'}
                  </div>
                </div>
                <div className="game-info">
                  <h3 className="game-title">{game.title}</h3>
                  <p className="game-developer">{game.developer_name || 'Unknown'}</p>
                  <div className="game-footer">
                    <span className="game-year">{game.release_year || 'N/A'}</span>
                    {game.avg_rating && (
                      <span className="rating-stars">
                        ★ {Number(game.avg_rating).toFixed(1)}
                      </span>
                    )}
                  </div>
                </div>
              </Link>
              
              {/* Library Button for quick status change */}
              <div style={{ padding: '0 1rem 1rem' }}>
                <LibraryButton 
                  userId={id} 
                  gameId={game.game_id} 
                  size="small"
                  onUpdate={fetchLibrary}
                />
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}
