import { useState, useEffect, useCallback } from 'react';
import RatingForm from './RatingForm';
import './Ratings.css';

export default function Ratings({ gameId, userId }) {
  const [reviews, setReviews] = useState([]);
  const [userRating, setUserRating] = useState(null);
  const [showForm, setShowForm] = useState(false);

  const fetchReviews = useCallback(async () => {
    try {
      const res = await fetch(`http://localhost:4000/ratings/${gameId}`);
      const data = await res.json();
      
      // Ensure data is an array
      setReviews(Array.isArray(data) ? data : []);
    } catch (err) {
      console.error('Error fetching reviews:', err);
      setReviews([]); // Set empty array on error
    }
  }, [gameId]);

  const fetchUserRating = useCallback(async () => {
    if (!userId) return;
    try {
      const res = await fetch(`http://localhost:4000/ratings/${gameId}/${userId}`);
      const data = await res.json();
      setUserRating(data);
    } catch (err) {
      console.error('Error fetching user rating:', err);
    }
  }, [gameId, userId]);

  useEffect(() => {
    fetchReviews();
    fetchUserRating();
  }, [fetchReviews, fetchUserRating]);

  const handleRatingSubmit = () => {
    setShowForm(false);
    fetchReviews();
    fetchUserRating();
  };

  const averageRating = reviews.length > 0
    ? (reviews.reduce((sum, r) => sum + r.rating, 0) / reviews.length).toFixed(1)
    : 'N/A';

  return (
    <div className="ratings-section">
      <h2>Ratings & Reviews</h2>
      
      <div className="rating-summary">
        <div className="average-rating">
          <span className="rating-number">{averageRating}</span>
          <span className="rating-label">/ 5.0</span>
          <span className="review-count">({reviews.length} reviews)</span>
        </div>

        {userId && (
          <button 
            className="write-review-btn"
            onClick={() => setShowForm(!showForm)}
          >
            {userRating ? 'Edit Your Review' : 'Write a Review'}
          </button>
        )}
      </div>

      {showForm && (
        <RatingForm
          userId={userId}
          gameId={gameId}
          existingRating={userRating}
          onSubmit={handleRatingSubmit}
          onCancel={() => setShowForm(false)}
        />
      )}

      <div className="reviews-list">
        {reviews.map((review) => (
          <div key={review.rating_id} className="review-card">
            <div className="review-header">
              <span className="reviewer-name">{review.username}</span>
              <span className="review-rating">‚≠ê {review.rating}/5</span>
            </div>
            {review.review_text && (
              <p className="review-text">{review.review_text}</p>
            )}
            <span className="review-date">
              {new Date(review.created_at).toLocaleDateString()}
            </span>
          </div>
        ))}
      </div>

      {reviews.length === 0 && (
        <p className="no-reviews">No reviews yet. Be the first to review this game!</p>
      )}
    </div>
  );
}
